$(function init(){var map=new AMap.Map("container",{center:[116.43255,39.939036],zoom:17});map.plugin(["AMap.ToolBar"],function(){map.addControl(new AMap.ToolBar)});var marker=new AMap.Marker({map:map,position:[116.43255,39.939036]});marker.setLabel({offset:new AMap.Pixel(20,20),content:"点击Marker打开高德地图"});marker.on("click",function(e){marker.markOnAMAP({name:"首开广场",position:marker.getPosition()})})});function myPosition(){map.plugin("AMap.Geolocation",function(){geolocation=new AMap.Geolocation({enableHighAccuracy:true,timeout:1e4,buttonOffset:new AMap.Pixel(10,20),zoomToAccuracy:true,buttonPosition:"RB"});map.addControl(geolocation);geolocation.getCurrentPosition()})}